;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_Start
;  FUNCTION NAME: DUALADC_SetPower
;
;  DESCRIPTION:
;  Applies power setting to the module's analog PSoc block.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;   A  Contains power level setting 0 to 3
;
;  RETURNS:  none
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified: 
;          CUR_PP
;
;  THEORY of OPERATION or PROCEDURE:
;
;-----------------------------------------------------------------------------
 DUALADC_Start:
_DUALADC_Start:
 DUALADC_SetPower:
_DUALADC_SetPower:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_PROLOGUE RAM_USE_CLASS_2
   RAM_SETPAGE_CUR >DUALADC_bfStatus

   and   A,DUALADC_bfPOWERMASK                       ; Mask only the valid power setting bits
   mov   X, SP                                       ; Get location of next location on stack
   push  A                                           ; Save power value on temp location

                                                     ; Set power for ADC1
   mov   A, reg[DUALADC_bfADC1cr3]                   ; Get current value of ADC1cr3
   and   A, ~DUALADC_bfPOWERMASK                     ; Mask off old power value
   or    A, [X]                                      ; OR in new power value
   or    A, f0h                                      ; Make sure other register is set correctly
   mov   reg[DUALADC_bfADC1cr3], A                   ; Reload CR with new power value

                                                     ; Set power for ADC2
   mov   A, reg[DUALADC_bfADC2cr3]                   ; Get current value of ADC2cr3
   and   A, ~DUALADC_bfPOWERMASK                     ; Mask off old power value
   or    A, [X]                                      ; OR in new power value
   or    A, f0h                                      ; Make sure other register is set correctly
   mov   reg[DUALADC_bfADC2cr3], A                   ; Reload CR with new power value

   tst   reg[DUALADC_bfADC1cr2], DUALADC_fRES_SET
   jz    .DoNotLoadRes
   mov   A,DUALADC_bNUMBITS - DUALADC_bMINRES                   ; get and set the resolution
   mov   [DUALADC_bfStatus], A              ; place it in the status variable
.DoNotLoadRes:

   pop   A                                           ; Restore the stack and power value
   RAM_EPILOGUE RAM_USE_CLASS_2
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_Stop
;
;  DESCRIPTION:
;  Removes power from the module's analog PSoc blocks, but the digital
;  blocks keep on running.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS: None
;
;  RETURNS:   NA
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;
 DUALADC_Stop:
_DUALADC_Stop:
   RAM_PROLOGUE RAM_USE_CLASS_1
   and   reg[DUALADC_bfADC1cr3], ~DUALADC_bfPOWERMASK
   and   reg[DUALADC_bfADC2cr3], ~DUALADC_bfPOWERMASK
   RAM_EPILOGUE RAM_USE_CLASS_1
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_Get_Samples
;
;  DESCRIPTION:
;  Starts the A/D convertor and will place data is memory.  A flag
;  is set whenever a new data value is available.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;  A  Number of samples to be taken.  A zero will cause the ADC to run
;     continuously.  A flag is set whenever a new data values are available.
;
;  RETURNS:  NA
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified: 
;          CUR_PP
;
 DUALADC_GetSamples:
_DUALADC_GetSamples:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC_bSampC
   mov   [DUALADC_bSampC], A                               ; Store sample count
                                                           ; Interrupts, Put A/D in reset
   mov   A,[DUALADC_bfStatus]                              ; get and set the resolution
   and   A,DUALADC_bRES_MASK
   add   A,DUALADC_bMINRES
   call  DUALADC_SetResolution

DUALADC_LoadMSBCounter:                                    ; The PWM has been setup by SetResolution, now set the upper
                                                           ; counter which will be the same as the period.
                                                           ; Reset MSB of counter to most negative value

   mov   A,reg[DUALADC_bPWM_IntTime_MSB]                   ; Get MSB of PWM and move it into RAM
   mov   [DUALADC_cCounter1U], A                           ; Use counter as temp location
   mov   A, 00h                                            ; Load A with zero for the calculation
   sub   A, [DUALADC_cCounter1U]                           ; 0 - MSB_PWM = MSB_of_most_neg_value
   asr   A                                                 ; Half the range (+ and -)
IF (DUALADC_DATA_FORMAT)
   mov   [DUALADC_cCounter1U], A                           ; Place result back into MSB of counter1
   mov   [DUALADC_cCounter2U], A                           ; Place result back into MSB of counter2
ELSE
   mov   [DUALADC_cCounter1U], 00h                         ; Always start at zero for unsigned values for ADC1
   mov   [DUALADC_cCounter2U], 00h                         ; Always start at zero for unsigned values for ADC2
ENDIF
   mov   A, reg[DUALADC_bPWM_IntTime_LSB]                  ; Dummy Read  - required do not remove
   mov   reg[DUALADC_bPeriod1], FFh                        ; Make sure counter1 starts at FF
   mov   reg[DUALADC_bPeriod2], FFh                        ; Make sure counter2 starts at FF

   and   reg[DUALADC_bfADC1cr3],~DUALADC_fFSW0             ; Take Integrator out of reset
   and   reg[DUALADC_bfADC2cr3],~DUALADC_fFSW0             ; Take Integrator out of reset
IF DUALADC_NoAZ
    and  reg[DUALADC_bfADC1cr2],~DUALADC_fAutoZero         ; Take Integrator out of AutoZero
    and  reg[DUALADC_bfADC2cr2],~DUALADC_fAutoZero         ; Take Integrator out of AutoZero
ENDIF

   or    reg[DUALADC_bCounter1_CR0], (DUALADC_fDBLK_ENABLE|DUALADC_fPULSE_WIDE)   ; Enable the Counter
   or    reg[DUALADC_bCounter2_CR0], (DUALADC_fDBLK_ENABLE|DUALADC_fPULSE_WIDE)   ; Enable the Counter
   or    reg[DUALADC_fPWM_LSB_CR0], DUALADC_fDBLK_ENABLE          ; Enable PWM
   or    reg[DUALADC_bfPWM16_INT_REG], DUALADC_bfPWM16_Mask    ; Enable PWM and Counter interrupts
   or    reg[DUALADC_bfCounter1_INT_REG], DUALADC_bfCounter1_Mask
   or    reg[DUALADC_bfCounter2_INT_REG], DUALADC_bfCounter2_Mask

   ret
   RAM_EPILOGUE RAM_USE_CLASS_4
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_StopAD
;
;  DESCRIPTION:
;  Completely shuts down the A/D is an orderly manner.  Both the
;  Timer and Counter are disabled and their interrupts are deactivated.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:  None
;
;  RETURNS: NA
;
;  SIDE EFFECTS:
;     The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;
 DUALADC_StopAD:
_DUALADC_StopAD:
   RAM_PROLOGUE RAM_USE_CLASS_1
   and   reg[DUALADC_fPWM_LSB_CR0], ~DUALADC_fDBLK_ENABLE      ; Disable the PWM

   and   reg[DUALADC_bCounter1_CR0], ~DUALADC_fDBLK_ENABLE           ; Disable the Counter
   and   reg[DUALADC_bCounter2_CR0], ~DUALADC_fDBLK_ENABLE           ; Disable the Counter

IF DUALADC_NoAZ
   or    reg[DUALADC_bfADC1cr2], DUALADC_fAutoZero             ; Put the Integrator into Autozero mode
   or    reg[DUALADC_bfADC2cr2], DUALADC_fAutoZero             ; Put the Integrator into Autozero mode
ENDIF

   or   reg[DUALADC_bfADC1cr3], DUALADC_fFSW0                  ; Put Integrator into reset
   or   reg[DUALADC_bfADC2cr3], DUALADC_fFSW0                  ; Put Integrator into reset

   push A
   M8C_DisableIntMask DUALADC_bfPWM16_INT_REG, DUALADC_bfPWM16_Mask      ; Disable interrupts
   M8C_DisableIntMask DUALADC_bfCounter1_INT_REG, DUALADC_bfCounter1_Mask
   M8C_DisableIntMask DUALADC_bfCounter2_INT_REG, DUALADC_bfCounter1_Mask
   pop  A
   
   RAM_EPILOGUE RAM_USE_CLASS_1
   ret
.ENDSECTION

.SECTION
