;  FUNCTION NAME: DUALADC_fIsData
;  FUNCTION NAME: DUALADC_fIsDataAvailable
;
;  DESCRIPTION:
;  Returns the status of the A/D Data is set whenever a new data
;  value is available.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS: None
;
;  RETURNS:
;  A  Returns data status  A == 0 no data available
;                          A != 0 data available
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified:
;          CUR_PP
;
 DUALADC_fIsData:
_DUALADC_fIsData:
 DUALADC_fIsDataAvailable:
_DUALADC_fIsDataAvailable:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC_bfStatus
   mov   A, [DUALADC_bfStatus]                        ; Get status byte
   and   A, DUALADC_fDATA_READY                       ; Mask off other bits
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_iGetData1ClearFlag
;  FUNCTION NAME: DUALADC_iGetData1
;
;  DESCRIPTION:
;  Returns the data from channel-1 of A/D.  Does not check if data is available.
;  iGetData1ClearFlag clears the result ready flag as well.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS: None
;
;  RETURNS:
;    A  Contains the LSB of the 16 bit result
;    X  Contains the MSB of the 16 bit result
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified:
;          CUR_PP
;
 DUALADC_iGetData1ClearFlag:
_DUALADC_iGetData1ClearFlag:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC_iResult1 
   and   [DUALADC_bfStatus], ~DUALADC_fDATA_READY  ; Clear Data ready bit
   mov   X, [(DUALADC_iResult1 + HighByte)]
   mov   A, [(DUALADC_iResult1 + LowByte)]
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret

 DUALADC_iGetData1:
_DUALADC_iGetData1:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC_iResult1  
   mov   X, [(DUALADC_iResult1 + HighByte)]
   mov   A, [(DUALADC_iResult1 + LowByte)]
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret
.ENDSECTION

