.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC_SetResolution
;
;  DESCRIPTION:
;  Sets A/D resolution between 7 and 13 bits.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;  A  Passes the number of bits of resolution, between 7 and 13.
;
;  RETURNS:  NA
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified: 
;          CUR_PP
;
;    This function halts the PWM and the counter to sync the A/D , but
;    does not re-enable the counter or PWM. To restart the A/D, "Get_Samples"
;    should be called. It sets the resolution for both channels at the
;    same time.
;
 DUALADC_SetResolution:
_DUALADC_SetResolution:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC_bfStatus  

   and   reg[DUALADC_bfADC1cr2], ~DUALADC_fRES_SET
   
   call  DUALADC_StopAD                            ; Stop the A/D if it is running
   mov   [DUALADC_bfStatus], 00h                   ; and clear status and old resolution

                                                   ; Check for resolution to be within min and max values
   cmp   A,DUALADC_bMINRES                         ; Check low end of resolution
   jnc   DUALADC_CHECKHI
   mov   A,DUALADC_bMINRES                         ; Too low - load legal low value
   jmp   DUALADC_RES_OK

DUALADC_CHECKHI:                                   ; Check high end of resolution
   cmp   A,DUALADC_bMAXRES
   jc    DUALADC_RES_OK
   mov   A,DUALADC_bMAXRES                         ; Too high - load legal Max value

DUALADC_RES_OK:
                                                   ; Calculate compare value for the PWM which
                                                   ; computes the integrate time
   sub   A, DUALADC_bMINRES                        ; Normalize with min resolution
   or    [DUALADC_bfStatus], A
                                                   ; Since min resolution is 7, 2^^7 = 128, the clock
                                                   ; is running 4x so 128*4=512 or 0x0200
   add   A,01h                                     ; The MSB is 02h.
   mov   X,A
   mov   A,01h

DUALADC_CALC_INTTIME:                              ; Now shift the MSB left for every bit of resolution of min (7).
   asl   A
   dec   X
   jnz   DUALADC_CALC_INTTIME

DUALADC_LOAD_INTTIME:                              ; Load compare value and Calc time into registers
                                                   ; Since minimum resolution is 7 bits, this value will always start at 0
   mov   reg[DUALADC_bPWM_IntTime_LSB], 00h
   mov   reg[DUALADC_bPWM_IntTime_MSB], A

                                                   ; Load the CalcTime into the PWM Period
   mov   reg[DUALADC_bPWM_Period_LSB], <wCalcTime
   add   A, >wCalcTime
   mov   reg[DUALADC_bPWM_Period_MSB],A
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret
.ENDSECTION

